CREATE OR REPLACE FUNCTION obter_se_ha_horario_prescr (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tem_horario_w	varchar(1):='N';
ie_horarios_gerados_w	varchar(1);

--Esta function verifica se existe horários que não estão suspensos para a prescrição, caso todos os horários estiverem suspensos, retorna N, caso contrário, se algum não estiver suspenso, retorna S
BEGIN
if ((obter_itens_prescr(nr_prescricao_p, null) IS NOT NULL AND (obter_itens_prescr(nr_prescricao_p, null))::text <> '')) then
	if (coalesce(nr_prescricao_p,0) > 0) then
		select 	CASE WHEN sum(qt_registros)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_horarios_gerados_w
		from (SELECT	count(nr_sequencia) qt_registros
			from	prescr_mat_hor
			where	nr_prescricao	= nr_prescricao_p
			
union

			SELECT	count(nr_sequencia) qt_registros
			from	prescr_proc_hor
			where	nr_prescricao	= nr_prescricao_p
			
union

			select	count(nr_sequencia) qt_registros
			from	prescr_dieta_hor
			where	nr_prescricao	= nr_prescricao_p
			
union

			select	count(nr_sequencia) qt_registros
			from	prescr_rec_hor
			where	nr_prescricao	= nr_prescricao_p) alias12;
		if (ie_horarios_gerados_w = 'S') then
			select 	CASE WHEN sum(qt_registros)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_tem_horario_w
			from (SELECT	count(nr_sequencia) qt_registros
				from	prescr_mat_hor
				where	nr_prescricao	= nr_prescricao_p
				and	coalesce(dt_suspensao::text, '') = ''
				
union

				SELECT	count(nr_sequencia) qt_registros
				from	prescr_proc_hor
				where	nr_prescricao	= nr_prescricao_p
				and	coalesce(dt_suspensao::text, '') = ''
				
union

				select	count(nr_sequencia) qt_registros
				from	prescr_dieta_hor
				where	nr_prescricao	= nr_prescricao_p
				and	coalesce(dt_suspensao::text, '') = ''
				
union

				select	count(nr_sequencia) qt_registros
				from	prescr_rec_hor
				where	nr_prescricao	= nr_prescricao_p
				and	coalesce(dt_suspensao::text, '') = '') alias10;
		else
			ie_tem_horario_w := 'S';
		end if;
	end if;
end if;

return	ie_tem_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
