CREATE OR REPLACE FUNCTION obter_se_hemoc_exige_justif ( nr_seq_exame_sangue_p bigint, nr_seq_derivado_p bigint, ie_irradiado_p text, ie_lavado_p text, ie_filtrado_p text, ie_porte_cirurgico_p text, qt_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_obriga_justif_w	varchar(1);

BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_obriga_justif_w
from	rep_hemocomp_justificativa
where	((nr_seq_exame_sangue_p = nr_seq_exame_sangue) or (coalesce(nr_seq_exame_sangue::text, '') = ''))
and	((nr_seq_derivado_p = nr_seq_derivado) or (coalesce(nr_seq_derivado::text, '') = ''))
and	((ie_irradiado_p = ie_irradiado) or (coalesce(ie_irradiado::text, '') = ''))
and	((ie_lavado_p	= ie_lavado) or (coalesce(ie_lavado::text, '') = ''))
and	((ie_filtrado_p	= ie_filtrado) or (coalesce(ie_filtrado::text, '') = ''))
and ((ie_porte_cirurgico_p = ie_porte_cirurgico) or (coalesce(ie_porte_cirurgico::text, '') = ''))
and ((qt_procedimento_p > qt_procedimento) or (coalesce(qt_procedimento::text, '') = ''))
and	ie_obriga_justif = 'S';

return	ie_obriga_justif_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
