CREATE OR REPLACE FUNCTION obter_se_higie_atrasada (dt_alta_p timestamp, dt_saida_real_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_retorno_w		bigint;
qt_minuto_w		bigint;
ie_atraso_w		varchar(1);
dt_higienizado_w	timestamp;

	/*
		Verifica se o leito está com a higienização atrasada conforme
		parâmetro [214] - Quantidade de minutos na pasta alta para atualizar a cor de alerta, caso não tenha início de higienização.
		da função Ocupação Hospitalar.
	*/
BEGIN

select	max(dt_inicio_higienizacao)
into STRICT	dt_higienizado_w
from	unidade_atendimento
where	cd_setor_atendimento = cd_setor_atendimento_p
and	cd_unidade_basica = cd_unidade_basica_p
and	coalesce(cd_unidade_compl,cd_unidade_compl_p) = cd_unidade_compl_p
and	dt_inicio_higienizacao >= dt_alta_p;

if (dt_alta_p IS NOT NULL AND dt_alta_p::text <> '') and (dt_saida_real_p IS NOT NULL AND dt_saida_real_p::text <> '') and (coalesce(dt_higienizado_w::text, '') = '') then
	begin
	select	max(obter_valor_param_usuario(44, 214, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p))
	into STRICT	qt_minuto_w
	;

	select	trunc(((clock_timestamp() - dt_saida_real_p) * 1440))
	into STRICT 	qt_retorno_w
	;

	if (qt_retorno_w > coalesce(qt_minuto_w,0)) then
		ie_atraso_w	:= 'S';
	else
		ie_atraso_w	:= 'N';
	end if;
	end;
else
	ie_atraso_w := 'N';

end if;

return	ie_atraso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
