CREATE OR REPLACE FUNCTION obter_se_hist_cob_visivel ( nr_seq_historico_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_visivel_w		varchar(1)	:= 'S';
qt_registro_w		bigint;

c01 CURSOR FOR
SELECT	'S'
from	tipo_hist_cob_regra_util a
where	a.nr_seq_tipo_hist			= nr_seq_historico_p
and	coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,coalesce(a.cd_estabelecimento,0))
and	coalesce(a.cd_perfil,cd_perfil_p)		= cd_perfil_p
and	coalesce(a.nm_usuario_lib,nm_usuario_p)	= nm_usuario_p;


BEGIN

if (nr_seq_historico_p IS NOT NULL AND nr_seq_historico_p::text <> '') and (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	tipo_hist_cob_regra_util
	where	nr_seq_tipo_hist	= nr_seq_historico_p;

	if (qt_registro_w > 0) then
		ie_visivel_w	:= 'N';

		open c01;
		loop
		fetch c01 into
			ie_visivel_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			null;
			end;
		end loop;
		close c01;
	end if;
end if;

return	ie_visivel_w;

end;
$body$
