CREATE OR REPLACE FUNCTION obter_se_hist_lib_usuario ( nr_seq_hist_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_regra_W		bigint;
qt_usuario_w		bigint;


BEGIN

ds_retorno_w := 'S';

select 	count(*)
into STRICT	qt_regra_w
from	cml_prospect_hist a,
	cml_prospect_hist_lib b
where	a.nr_sequencia = b.nr_seq_historico
and	a.nr_sequencia = nr_seq_hist_p;

if (qt_regra_w > 0) then
	begin

	select 	count(*)
	into STRICT	qt_usuario_w
	from	cml_prospect_hist a,
		cml_prospect_hist_lib b
	where	a.nr_sequencia = b.nr_seq_historico
	and	a.nr_sequencia = nr_seq_hist_p
	and	((b.nm_usuario_lib = nm_usuario_p) or (a.nm_usuario_nrec = nm_usuario_p));

	ds_retorno_w := 'S';
	if (qt_usuario_w = 0) then
		ds_retorno_w := 'N';
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
