CREATE OR REPLACE FUNCTION obter_se_homoc_prescr_atend ( nr_seq_derivado_p bigint, nr_atendimento_p bigint, nr_prescricao_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

/*
Retorno no formato: PRESCRICAO-IE_IRRADIADO-IE_FILTRADO-IE_ALIQUOTADO-IE_LAVADO-IE_AFERESE-IE_POOL;
*/
ds_lista_retorno_w	varchar(2000);

c01 CURSOR FOR
SELECT	a.nr_prescricao,
	b.dt_prescricao,
	coalesce(a.ie_filtrado,'N') ie_filtrado,
	coalesce(a.ie_irradiado,'N') ie_irradiado,
	coalesce(a.ie_aliquotado,'N') ie_aliquotado,
	coalesce(a.ie_lavado,'N') ie_lavado,
	coalesce(a.ie_aferese,'N') ie_aferese,
	coalesce(a.ie_pool,'N') ie_pool
from	prescr_medica b,
	prescr_procedimento a
where	a.nr_prescricao = b.nr_prescricao
and 	b.nr_prescricao <> nr_prescricao_p
and 	b.nr_atendimento = nr_atendimento_p
and 	((coalesce(a.ie_filtrado,'N')  = 'S') or (coalesce(a.ie_irradiado,'N')  = 'S') or (coalesce(a.ie_aliquotado,'N') = 'S') or (coalesce(a.ie_lavado,'N') = 'S') or (coalesce(a.ie_aferese,'N') = 'S') or (coalesce(a.ie_pool,'N') = 'S'))
and 	a.nr_seq_derivado = nr_seq_derivado_p

union

Select	a.nr_prescricao,
	b.dt_prescricao,
	coalesce(a.ie_filtrado,'N') ie_filtrado,
	coalesce(a.ie_irradiado,'N') ie_irradiado,
	coalesce(a.ie_aliquotado,'N') ie_aliquotado,
	coalesce(a.ie_lavado,'N') ie_lavado,
	coalesce(a.ie_aferese,'N') ie_aferese,
	coalesce(a.ie_pool,'N') ie_pool
from	prescr_medica b,
	prescr_procedimento a
where	a.nr_prescricao = b.nr_prescricao
and 	b.nr_prescricao <> nr_prescricao_p
and	b.nr_atendimento <> nr_atendimento_p
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
and 	((coalesce(a.ie_filtrado,'N')  = 'S') or (coalesce(a.ie_irradiado,'N')  = 'S') or (coalesce(a.ie_aliquotado,'N') = 'S') or (coalesce(a.ie_lavado,'N') = 'S') or (coalesce(a.ie_aferese,'N') = 'S') or (coalesce(a.ie_pool,'N') = 'S'))
and 	a.nr_seq_derivado = nr_seq_derivado_p
order by dt_prescricao, nr_prescricao;
c01_w	c01%rowtype;


BEGIN
ds_lista_retorno_w := null;
open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_lista_retorno_w :=   'NR_PRESCRICAO='||c01_w.nr_prescricao || '  ' || to_char(c01_w.dt_prescricao,'dd/mm/yyyy hh24:mi:ss') ||';'||
				'IE_IRRADIADO='||c01_w.ie_irradiado||';'||
				'IE_FILTRADO='||c01_w.ie_filtrado||';'||
				'IE_ALIQUOTADO='||c01_w.ie_aliquotado||';'||
				'IE_LAVADO='||c01_w.ie_lavado||';'||
				'IE_AFERESE='||c01_w.ie_aferese||';'||
				'IE_POOL='||c01_w.ie_pool||';';
	end;
end loop;
close C01;

return	ds_lista_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
