CREATE OR REPLACE FUNCTION obter_se_horario_aprazado ( ie_tipo_item_p text, dt_horario_p timestamp, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aprazado_w		varchar(1)	:= 'N';
nm_tabela_w		varchar(255);


BEGIN

nm_tabela_w	:= obter_tabela_tipo_item(ie_tipo_item_p);

if (nm_tabela_w	= 'PRESCR_DIETA') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_aprazado_w
	from	prescr_dieta_hor a,
		prescr_dieta_hor b
	where	a.nr_prescricao	= b.nr_prescricao
	and	a.nr_seq_dieta	= b.nr_seq_dieta
	and	a.nr_sequencia	<> b.nr_sequencia
	and	a.nr_sequencia	= nr_seq_horario_p
	and	b.dt_horario	= dt_horario_p
	and	coalesce(b.dt_suspensao::text, '') = ''
	and	coalesce(b.ie_situacao,'A')	<> 'I';
elsif (nm_tabela_w	= 'PRESCR_PROCEDIMENTO') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_aprazado_w
	from	prescr_proc_hor a,
		prescr_proc_hor b
	where	a.nr_prescricao	= b.nr_prescricao
	and	a.nr_seq_procedimento	= b.nr_seq_procedimento
	and	a.nr_sequencia	<> b.nr_sequencia
	and	a.nr_sequencia	= nr_seq_horario_p
	and	b.dt_horario	= dt_horario_p
	and	coalesce(b.dt_suspensao::text, '') = ''
	and	coalesce(b.ie_situacao,'A')	<> 'I';
elsif (nm_tabela_w	= 'PRESCR_MATERIAL') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_aprazado_w
	from	prescr_mat_hor a,
		prescr_mat_hor b
	where	a.nr_prescricao	= b.nr_prescricao
	and	a.nr_seq_material	= b.nr_seq_material
	and	a.nr_sequencia	<> b.nr_sequencia
	and	a.nr_sequencia	= nr_seq_horario_p
	and	b.dt_horario	= dt_horario_p
	and	coalesce(b.dt_suspensao::text, '') = ''
	and	coalesce(b.ie_situacao,'A')	<> 'I';
elsif (nm_tabela_w	= 'PRESCR_RECOMENDACAO') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_aprazado_w
	from	prescr_rec_hor a,
		prescr_rec_hor b
	where	a.nr_prescricao	= b.nr_prescricao
	and	a.nr_seq_recomendacao	= b.nr_seq_recomendacao
	and	a.nr_sequencia	<> b.nr_sequencia
	and	a.nr_sequencia	= nr_seq_horario_p
	and	b.dt_horario	= dt_horario_p
	and	coalesce(b.dt_suspensao::text, '') = ''
	and	coalesce(b.ie_situacao,'A')	<> 'I';
end if;

return	ie_aprazado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
