CREATE OR REPLACE FUNCTION obter_se_horario_atend_prescr (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_turno_p bigint, nr_seq_hora_p bigint, hr_inicial_p text, hr_final_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_turno_w		bigint;
hr_inicial_w		varchar(5);
hr_final_w		varchar(5);
dt_inicial_w		timestamp;
dt_final_w		timestamp;
dt_hora_inicio_w	timestamp;
dt_hora_final_w		timestamp;
dt_horario_w		timestamp;
dt_atual_w		timestamp;
ie_retorno_w		varchar(01);

C01 CURSOR FOR
	SELECT	a.dt_horario
	from	prescr_mat_hor a
	where	a.nr_prescricao	= nr_prescricao_p
	and	a.nr_seq_material	= nr_seq_material_p
	and	a.nr_seq_turno	= nr_seq_turno_p
	and	((nr_seq_hora_p = 0) or (a.nr_sequencia = nr_seq_hora_p))
	and	coalesce(a.dt_disp_farmacia::text, '') = ''
	and	Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S'
	order by a.dt_horario;


BEGIN

dt_hora_inicio_w		:= to_date('01/01/2000 '||hr_inicial_p||'00:00','dd/mm/yyyy hh24:mi:ss');
dt_hora_final_w			:= to_date('01/01/2000 '||hr_final_p||'00:59','dd/mm/yyyy hh24:mi:ss');

ie_retorno_w			:= 'N';

OPEN C01;
LOOP
	FETCH C01 INTO
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		dt_atual_w	:= to_date('01/01/2000 '|| to_char(dt_horario_w, 'hh24:mi') ||':00','dd/mm/yyyy hh24:mi:ss');

		if (ie_retorno_w = 'N') and (dt_atual_w >= dt_hora_inicio_w) and (dt_atual_w <= dt_hora_final_w) then
			ie_retorno_w	:= 'S';
		end if;

		end;
END LOOP;
CLOSE C01;


return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
