CREATE OR REPLACE FUNCTION obter_se_horario_comercial ( cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_horario_comercial_w		varchar(1) := 'N';
dt_referencia_w			timestamp := clock_timestamp();
qt_horas_w			smallint;


BEGIN

if (user = 'CORP') and (cd_estabelecimento_p = 61) then -- Philips Bangalore
	if (obter_se_horario_verao(dt_referencia_w) = 'S') then
		dt_referencia_w := dt_referencia_w + 7.50/24;
	else
		dt_referencia_w := dt_referencia_w + 8.50/24;
	end if;
end if;

if (pkg_date_utils.is_business_day(dt_referencia_w) = 0) then
	ie_horario_comercial_w := 'N';
elsif (obter_se_feriado(cd_estabelecimento_p, dt_referencia_w) > 0) then
	ie_horario_comercial_w := 'N';
else
	select	(to_char(dt_referencia_w, 'hh24'))::numeric
	into STRICT	qt_horas_w
	;

	if (user = 'CORP') and (cd_estabelecimento_p = 61) then -- Philips Bangalore
		if (qt_horas_w >= 9) and (qt_horas_w < 18) then
			ie_horario_comercial_w := 'S';
		end if;
	else
		if (qt_horas_w >= 8) and (qt_horas_w < 18) then
			ie_horario_comercial_w := 'S';
		end if;
	end if;
end if;

return ie_horario_comercial_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
