CREATE OR REPLACE FUNCTION obter_se_horario_conta (nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


cd_motivo_baixa_w	bigint;
cont_w			bigint;
ds_retorno_w		varchar(20);


BEGIN

select	max(cd_motivo_baixa)
into STRICT	cd_motivo_baixa_w
from	prescr_mat_hor
where	nr_sequencia	= nr_seq_horario_p
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

select	coalesce(count(*),0)
into STRICT	cont_w
from	tipo_baixa_prescricao
where	cd_tipo_baixa		= cd_motivo_baixa_w
and	ie_conta_paciente	= 'S';

ds_retorno_w	:= 'N';
if (cont_w	> 0) then
	ds_retorno_w	:= 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
