CREATE OR REPLACE FUNCTION obter_se_horario_prescricao ( nr_prescricao_p bigint, dt_inicio_p text, dt_fim_p text) RETURNS varchar AS $body$
DECLARE


ie_possui_horario_w	varchar(1):='N';


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) and (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '') and (dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_horario_w
	from	prescr_mat_hor
	where	nr_prescricao 	= nr_prescricao_p
	and	dt_horario	between	to_date(dt_inicio_p,'dd/mm/yyyy hh24:mi:ss') and to_date(dt_fim_p,'dd/mm/yyyy hh24:mi:ss');
end if;

return	ie_possui_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
