CREATE OR REPLACE FUNCTION obter_se_horario_valido_inter ( dt_horario_p timestamp, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE

ie_dia_semana_w		varchar(1);
ie_segunda_w		intervalo_prescricao.ie_segunda%type;
ie_terca_w			intervalo_prescricao.ie_terca%type;
ie_quarta_w			intervalo_prescricao.ie_quarta%type;
ie_quinta_w			intervalo_prescricao.ie_quinta%type;
ie_sexta_w			intervalo_prescricao.ie_sexta%type;
ie_sabado_w			intervalo_prescricao.ie_sabado%type;
ie_domingo_w		intervalo_prescricao.ie_domingo%type;
ie_situacao_w		varchar(1);


BEGIN

select	max(ie_segunda),
	max(ie_terca),
	max(ie_quarta),
	max(ie_quinta),
	max(ie_sexta),
	max(ie_sabado),
	max(ie_domingo)
into STRICT	ie_segunda_w,
	ie_terca_w,
	ie_quarta_w,
	ie_quinta_w,
	ie_sexta_w,
	ie_sabado_w,
	ie_domingo_w
from	intervalo_prescricao
where	cd_intervalo	= cd_intervalo_p;

select	pkg_date_utils.get_WeekDay(dt_horario_p)
into STRICT	ie_dia_semana_w
;

if	(ie_dia_semana_w	= 1 AND ie_domingo_w		= 'S') or
	(ie_dia_semana_w	= 2 AND ie_segunda_w		= 'S') or
	(ie_dia_semana_w	= 3 AND ie_terca_w		= 'S') or
	(ie_dia_semana_w	= 4 AND ie_quarta_w		= 'S') or
	(ie_dia_semana_w	= 5 AND ie_quinta_w		= 'S') or
	(ie_dia_semana_w	= 6 AND ie_sexta_w		= 'S') or
	(ie_dia_semana_w	= 7 AND ie_sabado_w		= 'S') then
	ie_situacao_w := 'A';
else
	ie_situacao_w := 'I';
end if;

return ie_situacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
