CREATE OR REPLACE FUNCTION obter_se_horario_verao (dt_referencia_p timestamp default clock_timestamp()) RETURNS varchar AS $body$
DECLARE


qt_reg_w		smallint;
ie_summer_time_w	varchar(10) := 'N';


BEGIN

begin
	select	count(1)
	into STRICT	qt_reg_w
	from	estab_horario_verao
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
	and	dt_referencia_p between dt_inicial and dt_final;

exception
when others then
	ie_summer_time_w := 'N';
end;

if (qt_reg_w > 0) then
	ie_summer_time_w	:= 'S';
end	if;

return	ie_summer_time_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
