SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_hora_agenda_disp ( cd_pessoa_fisica_p text, dt_agenda_p timestamp, nr_minuto_duracao_p bigint) RETURNS varchar AS $body$
DECLARE


dt_fim_agenda_w	timestamp;
dt_inicio_agenda_w  timestamp;
dt_agenda_w	timestamp;
ds_retorno_w	varchar(1) := 'S';



BEGIN

if 	((cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (nr_minuto_duracao_p IS NOT NULL AND nr_minuto_duracao_p::text <> '')) then

	dt_inicio_agenda_w	:= dt_agenda_p;
	dt_fim_agenda_w := dt_agenda_p + ((nr_minuto_duracao_p*60)/86400);


	select	coalesce(max('N'),'S')
	into STRICT	ds_retorno_w
	from	agenda_integrada_item a,
		agenda_consulta b
	where	a.nr_seq_agenda_cons = b.nr_sequencia
	and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	b.dt_agenda between pkg_date_utils.start_of(dt_agenda_p,'DD') and pkg_date_utils.end_of(dt_agenda_p,'DAY')
	and	((b.dt_agenda <= dt_inicio_agenda_w and (b.dt_agenda + (b.nr_minuto_duracao / 1440)) > dt_inicio_agenda_w) or (b.dt_agenda >  dt_inicio_agenda_w and b.dt_agenda < dt_fim_agenda_w));


	if (ds_retorno_w = 'S') then

		select	coalesce(max('N'),'S')
		into STRICT	ds_retorno_w
		from	agenda_integrada_item a,
			agenda_paciente b
		where	a.nr_seq_agenda_exame = b.nr_sequencia
		and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	b.hr_inicio between pkg_date_utils.start_of(dt_agenda_p,'DD') and pkg_date_utils.end_of(dt_agenda_p,'DAY')
		and	((b.hr_inicio <= dt_inicio_agenda_w and (b.hr_inicio + (b.nr_minuto_duracao / 1440)) > dt_inicio_agenda_w) or (b.hr_inicio >  dt_inicio_agenda_w and b.hr_inicio < dt_fim_agenda_w));


	end if;


end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
