CREATE OR REPLACE FUNCTION obter_se_hora_entre (dt_horario_p timestamp, dt_hora_inicio_p timestamp, dt_hora_fim_p timestamp) RETURNS varchar AS $body$
DECLARE

qt_ms_hora_w bigint;
qt_ms_hora_inicio_w bigint;
qt_ms_hora_fim_w bigint;

function obter_ms_hora_dia(dt_horario_pp	in 	timestamp) return bigint is
;
BEGIN
    --Obter total de milisegundos a partir 0 das horas do dia
    return (to_char(dt_horario_pp,'SSSSS'))::numeric;
end;

begin

qt_ms_hora_w := obter_ms_hora_dia(dt_horario_p);
qt_ms_hora_inicio_w := obter_ms_hora_dia(dt_hora_inicio_p);
qt_ms_hora_fim_w := obter_ms_hora_dia(dt_hora_fim_p);

if (qt_ms_hora_w = qt_ms_hora_inicio_w or qt_ms_hora_w = qt_ms_hora_fim_w) then
    return 'S';
elsif (qt_ms_hora_fim_w < qt_ms_hora_inicio_w) and (qt_ms_hora_w not between qt_ms_hora_fim_w and qt_ms_hora_inicio_w) then
    return 'S';
elsif (qt_ms_hora_fim_w > qt_ms_hora_inicio_w) and (qt_ms_hora_w between qt_ms_hora_inicio_w and qt_ms_hora_fim_w) then
    return 'S';
end if;

return 'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
