CREATE OR REPLACE FUNCTION obter_se_hora_extra ( dt_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w	integer;
ds_dia_semana_w	varchar(30);


BEGIN

select	upper(substr(obter_dia_semana(dt_referencia_p),1,3))
into STRICT	ds_dia_semana_w
;

if (ds_dia_semana_w in ('SÁB','DOM')) then
	return	'S';
else
	select	count(*)
	into STRICT	qt_reg_w
	from	feriado
	where	cd_estabelecimento = cd_estabelecimento_p
	and	trunc(dt_feriado,'dd') = trunc(dt_referencia_p,'dd');

	if (qt_reg_w > 0) then
		return	'S';
	else
		return	'N';
	end	if;
end	if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
