CREATE OR REPLACE FUNCTION obter_se_hora_periodo ( dt_inicial_p timestamp, dt_final_p timestamp, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_inicial_w	timestamp;
dt_final_w		timestamp;
dt_referencia_w	timestamp;
ie_periodo_w	varchar(1);


BEGIN
dt_inicial_w 	:= dt_inicial_p;
dt_final_w		:= dt_final_p;
dt_referencia_w	:= dt_referencia_p;

if (dt_inicial_w > dt_final_w) then
	dt_final_w := dt_final_w + 1;
	dt_referencia_w := dt_referencia_w + 1;
end if;

select	max(substr(obter_se_data_periodo(dt_referencia_p,dt_inicial_w,dt_final_w),1,1))
into STRICT	ie_periodo_w
;

if (coalesce(ie_periodo_w,'N') = 'N') then
	begin
	select	max(substr(obter_se_data_periodo(dt_referencia_w,dt_inicial_w,dt_final_w),1,1))
	into STRICT	ie_periodo_w
	;
	end;
end if;

return	ie_periodo_w;

end;
$body$
