CREATE OR REPLACE FUNCTION obter_se_hor_apap ( dt_medida_p timestamp, dt_referencia_p timestamp, dt_apap_p timestamp, cd_estabelecimento_p bigint, ie_24h_p text, ie_todos_p text) RETURNS varchar AS $body$
DECLARE


	function Obter_hor_apap
		return timestamp is
	hr_inicio_apap_w	timestamp;
	
BEGIN
	select	coalesce(max(to_date(to_char(dt_apap_p,'dd/mm/yyyy') || ' ' ||to_char(coalesce(hr_inicio_apap,to_date('01/01/2009 06:00:00','dd/mm/yyyy hh24:mi:ss')),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') +1/24),to_date('01/01/2009 06:00:00','dd/mm/yyyy hh24:mi:ss'))
	into STRICT	hr_inicio_apap_w
	from	parametro_medico
	where	cd_estabelecimento = cd_estabelecimento_p;
	return hr_inicio_apap_w;
	end;

begin

if (ie_todos_p	= 'N')	and (trunc(dt_medida_p,'hh24')	= trunc(dt_referencia_p,'hh24')) then
	return 'S';
elsif (ie_24h_p	= 'N')	and (ie_todos_p	= 'S') and (trunc(dt_medida_p,'hh24') between Obter_hor_apap and Obter_hor_apap + 1) then
	return 'S';
elsif (ie_24h_p	= 'S') and (trunc(dt_medida_p,'hh24') between trunc(dt_referencia_p -1,'hh24')  and trunc(dt_referencia_p +1/24,'hh24')) then
	return 'S';
end if;


return	'N';

end;
