CREATE OR REPLACE FUNCTION obter_se_hor_dose_especial ( nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_dose_especial_w	varchar(1) := 'N';


BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_dose_especial_w
	from	prescr_mat_hor w,
			prescr_material q where		q.nr_prescricao = w.nr_prescricao
	and		q.nr_sequencia = w.nr_seq_material
	and		(((q.qt_dose_especial = w.qt_dose) and (q.hr_dose_especial = to_char(w.dt_horario,'hh24:mi'))) or (coalesce(w.ie_dose_especial,'N') = 'S'))
	and		w.nr_sequencia = nr_seq_horario_p LIMIT 1;

end if;

return ie_dose_especial_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
