CREATE OR REPLACE FUNCTION obter_se_hor_prescr ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ie_possui_horario_w		varchar(1)	:= 'N';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_tipo_p in ('M','S')) then

	if (ie_tipo_p = 'M') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_possui_horario_w
		from	prescr_mat_hor
		where	nr_seq_material = nr_sequencia_p
		and		nr_prescricao = nr_prescricao_p;
	elsif (ie_tipo_p = 'S') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_possui_horario_w
		from	prescr_mat_hor
		where	nr_seq_solucao = nr_sequencia_p
		and		nr_prescricao = nr_prescricao_p;
	end if;
end if;

return	ie_possui_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
