CREATE OR REPLACE FUNCTION obter_se_hor_recomen_adm_data (nr_atendimento_p bigint, nr_prescricao_p bigint, cd_recomendacao_p bigint, dt_horario_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ds_retorno_w
from	prescr_recomendacao a,
	prescr_medica b,
	prescr_rec_hor c
where	a.nr_prescricao = b.nr_prescricao
and	a.nr_prescricao = c.nr_prescricao
and	a.nr_sequencia  = c.nr_seq_recomendacao
and	b.nr_atendimento = nr_atendimento_p
and	b.nr_prescricao = nr_prescricao_p
and	a.cd_recomendacao = cd_recomendacao_p
and	(c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> '')
and c.ds_horario = to_char(dt_horario_p,'hh24:mi');

return	ds_retorno_w;

end;
