CREATE OR REPLACE FUNCTION obter_se_hor_sepse_pend ( nr_seq_proc_p bigint, nr_seq_pe_prescr_p bigint, nr_seq_pe_proc_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1)	:= 'S';


BEGIN

SELECT 	coalesce(max('S'), 'N')
into STRICT	ie_retorno_w
FROM   	pe_prescr_proc_hor
WHERE  	nr_seq_proc = nr_seq_proc_p
AND	nr_seq_pe_prescr = nr_seq_pe_prescr_p
AND	nr_seq_pe_proc = nr_seq_pe_proc_p
AND	coalesce(dt_fim_horario::text, '') = ''
AND	coalesce(dt_suspensao::text, '') = ''
AND	ie_horario_especial <> 'S';

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
