CREATE OR REPLACE FUNCTION obter_se_hor_vigente_prescr ( dt_horario_p timestamp, nr_prescricoes_p text) RETURNS varchar AS $body$
DECLARE


ds_sql_w		varchar(2000);
ds_param_w		varchar(2000);
qt_prescr_w		varchar(10);


BEGIN
if (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') and (nr_prescricoes_p IS NOT NULL AND nr_prescricoes_p::text <> '') then

	ds_sql_w	:=
			' select	count(*) ' ||
			' from		prescr_medica ' ||
			' where		rownum = 1 ' ||
			' and		nr_prescricao in :nr_prescricoes_p ' ||
			' and		:dt_horario_p between dt_inicio_prescr and dt_validade_prescr ';

	ds_param_w	:=
			'dt_horario_p=' || to_char(dt_horario_p,'dd/mm/yyyy hh24:mi:ss') || ';';

	qt_prescr_w := obter_valor_dinamico_in_bv(ds_sql_w, ds_param_w, ':nr_prescricoes_p', nr_prescricoes_p, ',', qt_prescr_w);

end if;

if (qt_prescr_w > 0) then
	return 'S';
else
	return 'N';
end if;

end;
