CREATE OR REPLACE FUNCTION obter_se_houve_reacao_trans ( cd_pessoa_fisica_p bigint, nr_seq_producao_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_resultado_w varchar(2) := 'N';
nr_seq_evento_w	bigint;


BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_resultado_w 
from  san_transfusao c, 
		san_trans_reacao b, 
		san_producao a 
where 	a.nr_sequencia = b.nr_seq_producao 
and		c.nr_sequencia = a.nr_seq_transfusao 
and   b.nr_seq_producao = coalesce(nr_seq_producao_p, b.nr_seq_producao) 
and   c.cd_pessoa_fisica = cd_pessoa_fisica_p;
 
if (ie_resultado_w = 'N') then	 
	 
	nr_seq_evento_w := OBTER_PARAM_USUARIO(450, 339, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, nr_seq_evento_w);
	 
	select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT 	ie_resultado_w 
	from 	QUA_EVENTO_PACIENTE a, 
			atendimento_paciente b				 
	where 	a.nr_atendimento = b.nr_atendimento 
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	and	 	a.nr_seq_evento = nr_seq_evento_w 
	and	 	b.cd_pessoa_fisica = cd_pessoa_fisica_p;	
 
end if;
 
end if;
 
return ie_resultado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
