CREATE OR REPLACE FUNCTION obter_se_houve_reacao ( NR_SEQ_TRANSFUSAO_P bigint, NR_SEQ_PRODUCAO_P bigint) RETURNS varchar AS $body$
DECLARE


ie_resultado_w			varchar(1) := 'N';


BEGIN

select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT ie_resultado_w
from	san_trans_reacao b,
	san_producao a
where a.nr_sequencia		= b.nr_seq_producao
  and b.nr_seq_producao 	= coalesce(nr_seq_producao_p, nr_seq_producao)
  and a.nr_seq_transfusao	= nr_seq_transfusao_p;

RETURN ie_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
