CREATE OR REPLACE FUNCTION obter_se_hs_lib ( nr_sequencia_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

			 
			 
qt_reg_w	bigint;
nm_usuario_w	varchar(255);
cd_pessoa_fisica_w	varchar(10);

BEGIN
 
select	count(*) 
into STRICT	qt_reg_w 
from	historico_saude_lib 
where	NR_SEQ_HIST_SAUDE	= nr_sequencia_p;
 
if (qt_reg_w	> 0) then 
	 
	nm_usuario_w	:= Obter_Usuario_Ativo;
	 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	usuario 
	where	nm_usuario	= nm_usuario_w;
	 
 
	select	count(*) 
	into STRICT	qt_reg_w 
	from	historico_saude_lib 
	where	NR_SEQ_HIST_SAUDE				= nr_sequencia_p 
	and	coalesce(cd_perfil,cd_perfil_p)			 = cd_perfil_p 
	and	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0) 
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p)	 = cd_estabelecimento_p 
	and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_pessoa_fisica_w,cd_especialidade) = 'S'));
	 
	if (qt_reg_w	= 0) then 
		return 'N';
	end if;
	 
end if;
 
 
return	'S';
 
end;
