CREATE OR REPLACE FUNCTION obter_se_ie_situacao_tabela ( nr_sequencia_p bigint, nm_tabela_p text, ie_situacao_p text, nm_campo_seq_p text) RETURNS varchar AS $body$
DECLARE

/*
Retorna, caso a tabela passada por parâmetro(nm_tabela), se o campo ie_situacao possui valor igual ao passado por parâmetro(ie_situacao_p);
O parâmetro(nm_campo_seq_p) se refere a primary key da tabela da qual se quer fazer a validação, e o valor da PK deve ser passado por parâmetro(nr_sequencia_p);
Se passado o valor "T", trará retorno igual a 'S', se a tabela não possuir o campo também retornará 'S';
Para evitar demais tratamentos, só ira tratar o valor e retornará 'N' caso a tabela possua o campo IE_SITUACAO e o mesmo possuir valor diferente do passado por parâmetro(ie_situacao_p);
  */
ds_comando_w varchar(1000);
ds_parametros_w	varchar(1000);
ie_se_situacao_w varchar(1) := 'S';
ie_possui_atrib_w smallint;

BEGIN
if (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') and (ie_situacao_p IS NOT NULL AND ie_situacao_p::text <> '') and (ie_situacao_p <> 'T') then
	begin
	select	1
	into STRICT	ie_possui_atrib_w
	from	tabela_atributo
	where	upper(nm_tabela) = upper(nm_tabela_p)
	and	upper(nm_atributo) = 'IE_SITUACAO';
	if (ie_possui_atrib_w IS NOT NULL AND ie_possui_atrib_w::text <> '') then
		begin
		ds_comando_w := 'select decode(ie_situacao,:ie_situacao,''S'',''N'') from ' || nm_tabela_p || ' where ' || nm_campo_seq_p || ' = :nr_sequencia';
		ds_parametros_w := 'ie_situacao=' || ie_situacao_p || ';nr_sequencia='||nr_sequencia_p;
		ie_se_situacao_w := obter_valor_dinamico_char_bv(
				ds_comando_w, ds_parametros_w, ie_se_situacao_w);
		end;
	end if;
	end;
end if;
return	ie_se_situacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
