CREATE OR REPLACE FUNCTION obter_se_ignorar_dias_fut ( cd_pessoa_fisica_p text, cd_agenda_p bigint, cd_perfil_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w				varchar(1) := 'N';
cd_perfil_w				bigint;
ie_permite_w				varchar(1);
ie_permite_lib_w			varchar(1);

C01 CURSOR FOR
	SELECT coalesce(ie_ignora_regra_agend_fut,'N')
	from	agenda_regra_permissao
	where 	cd_agenda 					= cd_agenda_p
	and	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_p) 	= cd_pessoa_fisica_p
	and 	ie_situacao 					= 'A'
	and	coalesce(cd_perfil, cd_perfil_w) 			= cd_perfil_w
	order by cd_pessoa_fisica desc;


BEGIN

if (coalesce(cd_perfil_p::text, '') = '') then
	cd_perfil_w := 0;
else
	cd_perfil_w := cd_perfil_p;
end if;


open C01;
loop
fetch C01 into
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ie_retorno_w := ie_permite_w;
	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
