SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_imagem_evo_lib ( nr_seq_imagem_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(10):= 'S';
ie_tipo_evolucao_w	varchar(10);

qt_reg_w	bigint;


BEGIN
select	count(*)
into STRICT	qt_reg_w
from	evolucao_imagem_regra
where	nr_seq_imagem	= nr_seq_imagem_p;

if (qt_reg_w	> 0) then

	select	coalesce(max(ie_tipo_evolucao),'XPTO')
	into STRICT	ie_tipo_evolucao_w
	from	usuario
	where	nm_usuario	= nm_usuario_p;

	select	count(*)
	into STRICT	qt_reg_w
	from	evolucao_imagem_regra
	where	nr_seq_imagem	= nr_seq_imagem_p
	and	coalesce(ie_tipo_evolucao,ie_tipo_evolucao_w)	= ie_tipo_evolucao_w
	and	((coalesce(nr_seq_grupo::text, '') = '') or (OBTER_SE_USUARIO_GRUPO(nr_seq_grupo,nm_usuario)	= 'S'));

	if (qt_reg_w	= 0) then
		ie_retorno_w	:= 'N';
	end if;
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
