CREATE OR REPLACE FUNCTION obter_se_impede_adm (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_impedir_w			varchar(1) := 'S';
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
cont_w				integer;

c01 CURSOR FOR
SELECT	coalesce(ie_impedir,'S')
from	adep_impedir_adm_baixa a
where	coalesce(cd_material,cd_material_p) 			= cd_material_p
and	coalesce(a.cd_classe_material,cd_classe_material_w)	= cd_classe_material_w
and	coalesce(a.cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w
and	coalesce(a.cd_grupo_material,cd_grupo_material_w)	= cd_grupo_material_w
order by coalesce(cd_material,0),
		coalesce(a.cd_classe_material,0),
		coalesce(a.cd_subgrupo_material,0),
		coalesce(a.cd_grupo_material,0);


BEGIN

select	count(*)
into STRICT	cont_w
from	adep_impedir_adm_baixa;

if (cont_w > 0) then
	select	max(cd_grupo_material),
			max(cd_subgrupo_material),
			max(cd_classe_material)
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material	= cd_material_p;
	
	open C01;
	loop
	fetch C01 into	
		ie_impedir_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ie_impedir_w	:= ie_impedir_w;
	end loop;
	close C01;
	
end if;

return	coalesce(ie_impedir_w,'S');

end;
$body$
