CREATE OR REPLACE FUNCTION obter_se_impedimento ( cd_pessoa_fisica_p text, ie_impedimento_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	char(1);
cd_perfil_w		bigint;


BEGIN

cd_perfil_w	:=	obter_perfil_ativo;

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	pf_impedimento where		cd_pessoa_fisica = cd_pessoa_fisica_p
and		ie_impedimento = ie_impedimento_p
and		coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w
and		dt_inicio <= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())
and		coalesce(dt_final,clock_timestamp()) >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp()) LIMIT 1;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
