CREATE OR REPLACE FUNCTION obter_se_importa_plano ( cd_egk_p text, dt_nascimento_p timestamp, ds_given_name_p text, ds_family_name_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_egk_w			varchar(30);
dt_nascimento_w		timestamp;
ds_given_name_w		varchar(255);
ds_family_name_w	varchar(255);

c01 CURSOR FOR
SELECT	cd_usuario_convenio
from 	pessoa_titular_convenio
where 	cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN

	open C01;
	loop
	fetch C01 into
		cd_egk_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			if (cd_egk_w = cd_egk_p) then
				return 'S';
			end if;
		end;
	end loop;
	close C01;


	select 	a.ds_given_name,
			a.ds_family_name,
			b.dt_nascimento
	into STRICT	ds_given_name_w,
			ds_family_name_w,
			dt_nascimento_w
	from   	person_name a,
			pessoa_fisica b
	where  	b.nr_seq_person_name = a.nr_sequencia
	and    	a.ds_type = 'main'
	and	   	b.cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (pkg_date_utils.get_DiffDate(dt_nascimento_w, dt_nascimento_p, 'DAY') = 0 and
		ds_given_name_w = ds_given_name_p and
		ds_family_name_w = ds_family_name_p)then
		return 'S';
	end if;

	return 'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
