SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_imprime_anamnese ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w		varchar(01) := 'N';
ie_preencher_dt_autom_w	varchar(01);
nr_atendimento_w	bigint;
cd_medico_w		varchar(10);
ie_medico_w		varchar(01);
ie_medico_resp_w	varchar(10);
ie_setor_PA_w		integer;
ie_relatorio_w		varchar(01);
dt_fim_consulta_w	timestamp;
cd_setor_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
			

BEGIN 
if (nr_sequencia_p > 0) then 
	ie_preencher_dt_autom_w := Obter_Param_Usuario(935, 169, Obter_perfil_Ativo, obter_usuario_ativo, wheb_usuario_pck.get_cd_estabelecimento, ie_preencher_dt_autom_w);
	ie_relatorio_w := Obter_Param_Usuario(935, 223, Obter_perfil_Ativo, obter_usuario_ativo, wheb_usuario_pck.get_cd_estabelecimento, ie_relatorio_w);
	 
	if (ie_preencher_dt_autom_w = 'S') and (ie_relatorio_w = 'S' or ie_relatorio_w = 'I') then 
	 
		select	max(a.nr_atendimento), 
			max(a.cd_medico_resp), 
			max(obter_setor_atendimento(a.nr_atendimento)) 
		into STRICT	nr_atendimento_w, 
			cd_medico_w, 
			cd_setor_atendimento_w 
		FROM	atendimento_ps_v a, 
			anamnese_paciente b 
		WHERE	a.nr_atendimento = b.nr_atendimento 
		AND 	b.nr_sequencia = nr_sequencia_p;
		 
		select 	max(coalesce(('S'),'N')) 
		into STRICT	ie_medico_w 
		from	Medico 
		where 	cd_pessoa_fisica = cd_medico_w;
		 
		if (ie_medico_w = 'S') then 
		 
			select	max(cd_pessoa_fisica) 
			into STRICT	cd_pessoa_fisica_w 
			from	usuario 
			where	nm_usuario = obter_usuario_ativo;
			 
			if (cd_pessoa_fisica_w = cd_medico_w) then 
				ie_medico_resp_w := 'S';
			else 
				ie_medico_resp_w := 'N';
			end if;
			 
			select	count(*) 
			into STRICT	ie_setor_PA_w 
			from	setor_atendimento 
			where	cd_setor_atendimento = cd_setor_atendimento_w 
			and	cd_classif_setor = '1';
			 
			select 	MAX(dt_fim_consulta) 
			into STRICT 	dt_fim_consulta_w 
			from	atendimento_ps_v 
			where	nr_atendimento = nr_atendimento_w;
			 
			if (ie_setor_PA_w > 0) and (dt_fim_consulta_w IS NOT NULL AND dt_fim_consulta_w::text <> '') and (ie_medico_resp_w = 'S') 	then 
			 
				ie_retorno_w := 'S';
			end if;
		end if;
	end if;
end if;
RETURN 	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
