CREATE OR REPLACE FUNCTION obter_se_imprime_prescr_turno ( nr_prescricao_p bigint, nr_seq_turno_p bigint, cd_local_estoque_p bigint, qt_hora_antes_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_imprime_w			bigint 	:= 0;


BEGIN 
 
select	count(*) 
into STRICT	qt_imprime_w 
from	prescr_mat_hor b, 
	prescr_material x, 
	prescr_medica a 
where	a.nr_prescricao	= nr_prescricao_p 
and	a.nr_prescricao = x.nr_prescricao 
and	x.nr_prescricao = b.nr_prescricao 
and	x.nr_sequencia	= b.nr_seq_material 
and	a.nr_prescricao	= b.nr_prescricao 
and	b.nr_seq_turno	= nr_seq_turno_p 
and	coalesce(a.dt_emissao_farmacia::text, '') = '' 
and	coalesce(b.dt_emissao_farmacia::text, '') = '' 
and	coalesce(b.ie_dispensar_farm,'S') = 'S' 
and	b.QT_DISPENSAR_HOR > 0 
and	dt_horario	<= Obter_data_fim_turno(b.nr_prescricao,nr_seq_turno_p,qt_hora_antes_p) 
and substr(obter_se_imprimi_turno(nr_prescricao_p,nr_seq_turno_p,qt_hora_antes_p,dt_horario),1,1) = 'S' 
and	((x.cd_local_estoque = cd_local_estoque_p) or (coalesce(x.cd_local_estoque::text, '') = '')) 
and	Obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S';
 
return qt_imprime_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
