CREATE OR REPLACE FUNCTION obter_se_inativa_item_pront ( nm_usuario_p text, nr_seq_item_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';


BEGIN

    select  coalesce(max(a.ie_inativar),'S')
    into STRICT    ds_retorno_w
    from    perfil_item_pront_usuario a
    where   a.nm_usuario_param = nm_usuario_p
    and     a.nr_seq_item_perfil = nr_seq_item_perfil_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
