CREATE OR REPLACE FUNCTION obter_se_inativa_tipo_evol (cd_tipo_evolucao_p text) RETURNS varchar AS $body$
DECLARE

ie_inativar_w	varchar(20);
cd_perfil_w	bigint;


C01 CURSOR FOR
	SELECT	ie_inativar
	from	TIPO_EVOLUCAO_INAT
	where	cd_tipo_evolucao 					= cd_tipo_evolucao_p
	and	coalesce(cd_perfil, 		coalesce(cd_perfil_w,0)) 		= coalesce(cd_perfil_w,0)
	order by 	coalesce(cd_perfil, 0);

BEGIN
cd_perfil_w	:= obter_perfil_ativo;

open C01;
loop
fetch C01 into
	ie_inativar_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	ie_inativar_w;

end;
$body$
