CREATE OR REPLACE FUNCTION obter_se_inclui_protocolo (nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_interno_conta_w	bigint;
ie_mostrar_protocolo_w	varchar(10);

c01 CURSOR FOR 
SELECT	'S' 
from	titulo_receber a 
where	a.nr_seq_protocolo	= nr_seq_protocolo_p 
and	a.vl_saldo_titulo	> 0;

c02 CURSOR FOR 
SELECT	nr_interno_conta 
from	conta_paciente a 
where	a.nr_seq_protocolo	= nr_seq_protocolo_p;

c03 CURSOR FOR 
SELECT	'S' 
from	titulo_receber a 
where	a.nr_interno_conta	= nr_interno_conta_w 
and	a.vl_saldo_titulo	> 0;


BEGIN 
 
ie_mostrar_protocolo_w	:= 'N';
 
open c01;
loop 
fetch c01 into 
	ie_mostrar_protocolo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_mostrar_protocolo_w	:= ie_mostrar_protocolo_w;
end loop;
close c01;
 
if (ie_mostrar_protocolo_w = 'N') then 
	open c02;
	loop 
	fetch c02 into 
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	 
		open c03;
		loop 
		fetch c03 into 
			ie_mostrar_protocolo_w;
		EXIT WHEN NOT FOUND; /* apply on c03 */
			ie_mostrar_protocolo_w	:= ie_mostrar_protocolo_w;
		end loop;
		close c03;
 
	end loop;
	close c02;
end if;
 
return coalesce(ie_mostrar_protocolo_w, 'N');
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
