CREATE OR REPLACE FUNCTION obter_se_inclusao_reg_alerta ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


dt_acesso_w		timestamp	:= clock_timestamp();
dt_alerta_w		timestamp	:= clock_timestamp();
ds_retorno_w		varchar(255)	:= 'N';


BEGIN

if (((coalesce(nr_atendimento_p,0) > 0) or (coalesce(cd_pessoa_fisica_p,'XPTO') <> 'XPTO')) and (coalesce(nm_usuario_p,'XPTO') <> 'XPTO')) then
	if (coalesce(nr_atendimento_p, 0) > 0) then
		select	max(dt_atualizacao_nrec)
		into STRICT	dt_acesso_w
		from	pep_acesso
		where	nr_atendimento = nr_atendimento_p
		and	nm_usuario = nm_usuario_p
		and	trunc(dt_atualizacao) = trunc(clock_timestamp());

		select  max(dt_insercao)
		into STRICT    dt_alerta_w
		from    alerta_v
		where 	nr_atendimento = nr_atendimento_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select	max(dt_atualizacao_nrec)
		into STRICT	dt_acesso_w
		from    pep_acesso
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	nm_usuario = nm_usuario_p
		and	trunc(dt_atualizacao) = trunc(clock_timestamp());

		select  max(dt_insercao)
		into STRICT    dt_alerta_w
		from    alerta_v
		where 	cd_pessoa_alerta	= cd_pessoa_fisica_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');
	end if;
end if;

if (dt_alerta_w > dt_acesso_w) or (coalesce(dt_acesso_w::text, '') = '') then
	ds_retorno_w	:= 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
