SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_inconsistente (ie_tipo_consiste_p bigint, nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE

				
nr_retorno_w		bigint := 0;
nr_atendimento_w	bigint;

/*
ie_tipo_consiste_p:
1 - Retorna valor se existirem inconsistências na conta;
2 - Retorna valor se existirem inconsistências que não permitem fechar a conta;
3 - Retorna valor se existirem inconsistências que não permitem fechar a conta em alguma conta do atendimento;
*/
BEGIN

if (ie_tipo_consiste_p = 1) then
	select	count(*)
	into STRICT	nr_retorno_w
	from	sus_inconsistencia b, 
		sus_consistencia_conta a 
	where	a.nr_seq_inconsistencia = b.nr_sequencia 
	and	a.nr_interno_conta = nr_interno_conta_p 
	and	coalesce(Sus_Obter_Se_Consiste_Estab(b.nr_sequencia, a.cd_estabelecimento, 'C'),b.ie_consiste) = 'S';
elsif (ie_tipo_consiste_p = 2) then
	select	count(*)
	into STRICT	nr_retorno_w 
	from	sus_inconsistencia b, 
		sus_consistencia_conta a,
		conta_paciente c
	where	a.nr_seq_inconsistencia = b.nr_sequencia 
	and	c.nr_interno_conta = a.nr_interno_conta
	and	a.nr_interno_conta = nr_interno_conta_p 
	and	coalesce(Sus_Obter_Se_Consiste_Estab(b.nr_sequencia, a.cd_estabelecimento, 'C'),b.ie_consiste) = 'S' 
	and	coalesce(Sus_Obter_Se_Consiste_Estab(b.nr_sequencia, a.cd_estabelecimento, 'L'),b.ie_permite_liberar) = 'N'
	and	coalesce(c.ie_cancelamento::text, '') = '';
elsif (ie_tipo_consiste_p = 3) then
	select	coalesce(max(nr_atendimento),0)
	into STRICT	nr_atendimento_w
	from	conta_paciente 
	where	nr_interno_conta = nr_interno_conta_p;

	select	count(*)
	into STRICT	nr_retorno_w
	from	sus_inconsistencia b, 
		sus_consistencia_conta a, 
		conta_paciente c 
	where	a.nr_seq_inconsistencia = b.nr_sequencia 
	and	c.nr_interno_conta = a.nr_interno_conta 
	and	c.nr_atendimento = nr_atendimento_w 
	and	coalesce(Sus_Obter_Se_Consiste_Estab(b.nr_sequencia, a.cd_estabelecimento, 'C'),b.ie_consiste) = 'S' 
	and	coalesce(Sus_Obter_Se_Consiste_Estab(b.nr_sequencia, a.cd_estabelecimento, 'L'),b.ie_permite_liberar) = 'N'
	and	coalesce(c.ie_cancelamento::text, '') = '';
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
