CREATE OR REPLACE FUNCTION obter_se_inconsiste_laudo_aih ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_classificacao_p bigint, cd_cid_principal_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
qt_cid_compat_w			bigint;
nr_seq_geral_w			bigint;


BEGIN

/* 2 - CID diagn√≥stico principal incorreto */

if (coalesce(cd_procedimento_p,0) <> 0) and (coalesce(ie_origem_proced_p,0) <> 0) and (coalesce(ie_classificacao_p,0) = 1) and (coalesce(cd_cid_principal_p,'X') <> 'X') then
	begin
	select	count(*)
	into STRICT	qt_cid_compat_w
	from	sus_procedimento_cid
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_geral_w
	from	sus_procedimento_cid
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	cd_doenca_cid		= cd_cid_principal_p;

	if (nr_seq_geral_w	= 0) and (qt_cid_compat_w <> 0) then
		ds_retorno_w := 'S';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
