CREATE OR REPLACE FUNCTION obter_se_infec_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(60);
nr_atendimento_w	bigint;
qt_tmp_w		bigint;
qt_infec_w		bigint;


BEGIN

if (nr_atendimento_p > 0) then


	select	count(*)
	into STRICT	qt_tmp_w
	from	cih_ficha_ocorrencia f,
		cih_local_infeccao i
	where	f.nr_ficha_ocorrencia	= i.nr_ficha_ocorrencia
	and	f.nr_atendimento	= nr_atendimento_p;


	if (qt_tmp_w	>0) then
		return 'S';
	end if;


end if;


return	'N';

end;
$body$
LANGUAGE PLPGSQL
