CREATE OR REPLACE FUNCTION obter_se_informacao_sensivel (nm_tabela_p text, nm_atributo_p text) RETURNS varchar AS $body$
DECLARE


ie_informacao_sensivel_w	tabela_atributo.ie_informacao_sensivel%type;
	

BEGIN

ie_informacao_sensivel_w := 'N';

if   (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '' AND nm_atributo_p IS NOT NULL AND nm_atributo_p::text <> '') then

    select	coalesce(max(IE_INFORMACAO_SENSIVEL), 'N')
    into STRICT	ie_informacao_sensivel_w
    from	tabela_atributo
    where	nm_tabela = nm_tabela_p
    and	nm_atributo = nm_atributo_p;

end	if;

return	ie_informacao_sensivel_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
