CREATE OR REPLACE FUNCTION obter_se_inf_autorizada (cd_tipo_informacao_p text) RETURNS varchar AS $body$
DECLARE


qt_controle_w smallint;


BEGIN

select	count(*)
into STRICT	qt_controle_w
from	autorizacao_inf_cliente
where	cd_tipo_informacao = cd_tipo_informacao_p;

