CREATE OR REPLACE FUNCTION obter_se_iniciais_paciente ( nm_pessoa_fisica_p text, ie_obs_nm_consig_p text) RETURNS varchar AS $body$
DECLARE


ds_iniciais	varchar(20);


BEGIN
	
	if (ie_obs_nm_consig_p = 'S') then
