CREATE OR REPLACE FUNCTION obter_se_iniciar_analise_gpt ( nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_prescricao_w			integer;
ie_retorno_w			varchar(1);
dt_inicio_prescricao_w	timestamp;
					

BEGIN

qt_prescricao_w	 		:= 0;
ie_retorno_w	 		:= 'N';
dt_inicio_prescricao_w 	:= (clock_timestamp() - interval '7 days');

select	count(*)
into STRICT	qt_prescricao_w
from	prescr_medica
where	nr_atendimento 		= nr_atendimento_p
and		cd_pessoa_fisica 	= cd_pessoa_fisica_p
and		(dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> '')
and		coalesce(dt_liberacao::text, '') = ''
and		coalesce(dt_inicio_analise_farm::text, '') = ''
and		coalesce(nm_usuario_analise_farm::text, '') = ''
and 	dt_prescricao > dt_inicio_prescricao_w;

if (qt_prescricao_w = 0) then
	
	select	count(*)
	into STRICT 	qt_prescricao_w
	from	prescr_medica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and		coalesce(nr_atendimento::text, '') = ''
	and		(dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> '')
	and		coalesce(dt_liberacao::text, '') = ''
	and		coalesce(dt_inicio_analise_farm::text, '') = ''
	and		coalesce(nm_usuario_analise_farm::text, '') = ''
	and 	dt_prescricao > dt_inicio_prescricao_w;
	
end if;

if (qt_prescricao_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
