CREATE OR REPLACE FUNCTION obter_se_inicio_solucao_rev (nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_w		varchar(1) := 'N';
nr_seq_ult_evento_w	bigint;
nr_seq_ant_evento_w	bigint;
ie_ult_alteracao_w	smallint;
ie_ant_alteracao_w	smallint;
qt_evento_w		bigint;
qt_evento_invalido_w	bigint;
qt_evento_reversao_w	bigint;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_ult_evento_w
	from	prescr_solucao_evento
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_p;

	if (nr_seq_ult_evento_w > 0) then
		select	ie_alteracao
		into STRICT	ie_ult_alteracao_w
		from	prescr_solucao_evento
		where	nr_sequencia = nr_seq_ult_evento_w;

		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_ant_evento_w
		from	prescr_solucao_evento
		where	nr_prescricao		= nr_prescricao_p
		and	nr_seq_solucao	= nr_seq_solucao_p
		and	nr_sequencia		< nr_seq_ult_evento_w;

		if (nr_seq_ant_evento_w > 0) then
			select	ie_alteracao
			into STRICT	ie_ant_alteracao_w
			from	prescr_solucao_evento
			where	nr_sequencia = nr_seq_ant_evento_w;

			if (ie_ult_alteracao_w = 6) and (ie_ant_alteracao_w = 1) then
				ie_status_w := 'S';
			else
				select	count(*)
				into STRICT	qt_evento_w
				from	prescr_solucao_evento
				where	nr_prescricao		= nr_prescricao_p
				and	nr_seq_solucao 	= nr_seq_solucao_p;

				select	count(*)
				into STRICT	qt_evento_invalido_w
				from	prescr_solucao_evento
				where	nr_prescricao		= nr_prescricao_p
				and	nr_seq_solucao 	= nr_seq_solucao_p
				and	ie_evento_valido	= 'N'
				and	ie_alteracao 		in (1,2,3,4,5);

				select	count(*)
				into STRICT	qt_evento_reversao_w
				from	prescr_solucao_evento
				where	nr_prescricao		= nr_prescricao_p
				and	nr_seq_solucao 	= nr_seq_solucao_p
				and	ie_alteracao 		= 6;

				if	(qt_evento_w - (qt_evento_invalido_w + qt_evento_reversao_w) = 0) then
					ie_status_w := 'S';
				end if;
			end if;
		end if;
	end if;
end if;

return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
