CREATE OR REPLACE FUNCTION obter_se_insere_carta_cid ( nr_seq_modelo_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';
ie_exige_w	 	varchar(1);
ie_usa_case_w	varchar(1);


BEGIN


select 	coalesce(ie_exige_cid,'N'),
		coalesce(ie_metodo_busca_info,'N')
into STRICT	ie_exige_w,
		ie_usa_case_w
from 	carta_medica_modelo
where 	nr_sequencia = nr_seq_modelo_p;

if (ie_exige_w = 'S')then
	if (ie_usa_case_w = 'S')then
		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	diagnostico_doenca
		where	nr_atendimento in (	SELECT	a.nr_atendimento 
									from 	atendimento_paciente a, 
											episodio_paciente b 
									where 	a.nr_seq_episodio = b.nr_sequencia 
									and 	b.nr_sequencia = obter_episodio_atendimento(nr_atendimento_p))
		and 	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and 	coalesce(dt_inativacao::text, '') = '';
	else
		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	diagnostico_doenca
		where	nr_atendimento = nr_atendimento_p
		and 	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and 	coalesce(dt_inativacao::text, '') = '';
	end if;
	
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
