CREATE OR REPLACE FUNCTION obter_se_inserir_conta ( nr_atendimento_p bigint, nr_seq_protocolo_p bigint, nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		varchar(1) := 'N';
dt_entrada_w		timestamp;
dt_mesano_referencia_w	timestamp;


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then 
	 
	select	trunc(dt_entrada) 
	into STRICT	dt_entrada_w 
	from	atendimento_paciente 
	where	((nr_atendimento = nr_atendimento_p) or (nr_atendimento = obter_atendimento_conta(nr_interno_conta_p)));
	 
	select	dt_mesano_referencia 
	into STRICT	dt_mesano_referencia_w 
	from	protocolo_convenio 
	where	nr_seq_protocolo = nr_seq_protocolo_p;
	 
	if (dt_entrada_w > dt_mesano_referencia_w) then 
		ie_retorno_w := 'S';
	end if;
	 
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
