SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_inspecao_liberada ( nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


/* Essa function verifica se todas as inspeções da ordem de compra (passada por parâmetro) estão liberadas  */

qt_inspecao_w		integer;
qt_ordem_w		integer;
ds_resultado_w		varchar(1);


BEGIN
ds_resultado_w := 'N';

select	count(*)
into STRICT	qt_inspecao_w
from	inspecao_recebimento
where	nr_ordem_compra = nr_ordem_compra_p;

select	count(*)
into STRICT	qt_ordem_w
from	inspecao_recebimento
where	nr_ordem_compra = nr_ordem_compra_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

if (qt_inspecao_w = qt_ordem_w) then
	ds_resultado_w := 'S';
end if;

return	ds_resultado_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_inspecao_liberada ( nr_ordem_compra_p bigint) FROM PUBLIC;

