CREATE OR REPLACE FUNCTION obter_se_instala_sol_palm (nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_instalada_w		varchar(1);
qtd_w			integer  := 0;
dt_preparo_w		timestamp;
dt_paciente_w		timestamp;
ie_permite_instalar_w 	varchar(1) := 'N';
nr_seq_procedimento_w	integer;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_etapa_p IS NOT NULL AND nr_etapa_p::text <> '') then

	ie_instalada_w := obter_se_solucao_instalada(nr_prescricao_p, nr_seq_solucao_p, nr_etapa_p);
	--Verificar se a solução está preparada
	if (ie_instalada_w = 'N') then
	begin
		select 	count(*)
		into STRICT	qtd_w
		from   	adep_processo
		where  	nr_prescricao  	= nr_prescricao_p
		and	nr_seq_solucao 	= nr_seq_solucao_p
		and    	nr_etapa 	= nr_etapa_p;

		if (qtd_w > 0) then
			select 	max(dt_preparo),
				max(dt_paciente),
				max(nr_seq_procedimento)
			into STRICT	dt_preparo_w,
				dt_paciente_w,
				nr_seq_procedimento_w
			from   	adep_processo
			where  	nr_prescricao  	= nr_prescricao_p
			and	nr_seq_solucao 	= nr_seq_solucao_p
			and    	nr_etapa 	= nr_etapa_p;
			if ( ((dt_preparo_w IS NOT NULL AND dt_preparo_w::text <> '') or (nr_seq_procedimento_w IS NOT NULL AND nr_seq_procedimento_w::text <> ''))and coalesce(dt_paciente_w::text, '') = '') then
				ie_permite_instalar_w := 'S';
			end if;
		end if;
	end;
	end if;
end if;

return ie_permite_instalar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
